import * as actionTypes from '../actions/actionTypes'; 
import {updateObject} from '../../shared/utility'; 

const initialState = {
	sensorList: [],
	sensorValues: [], 
	loading: false,
	error: null
}

const getSensorListStart = (state, action) => {
	return updateObject(state, {
		loading: true, 
		error: null
	})
}

const getSensorListSuccess = (state, action) => {
	return updateObject(state, {
		sensorList: action.sensorList,
		loading: false
	});
}

const getSensorListFail = (state, action) => {
	return updateObject(state, {
		loading: false,
		error: action.error
	})
}

const getSensorValuesStart = (state, action) => {
	return updateObject(state, {
		loading: true, 
		error: null
	})
}

const getSensorValuesSuccess = (state, action) => {
	console.log('getSensorValuesSuccess', getSensorValuesSuccess); 
	return updateObject(state, {
		sensorValues: action.sensorValues,
		loading: false
	});
}

const getSensorValuesFail = (state, action) => {
	return updateObject(state, {
		loading: false,
		error: action.error
	})
}

const weatherDataReducer = (state = initialState, action) => {
	switch(action.type){
		case (actionTypes.GET_SENSOR_LIST_START): return getSensorListStart(state, action);
		case (actionTypes.GET_SENSOR_LIST_SUCCESS): return getSensorListSuccess(state, action);
		case (actionTypes.GET_SENSOR_LIST_FAIL): return getSensorListFail(state, action);

		case (actionTypes.GET_SENSOR__VALUES_START): return getSensorValuesStart(state, action);
		case (actionTypes.GET_SENSOR__VALUES_SUCCESS): return getSensorValuesSuccess(state, action);
		case (actionTypes.GET_SENSOR__VALUES_FAIL): return getSensorValuesFail(state, action);

		default: return state;
	}
}

export default weatherDataReducer;
